import { default as React, PropTypes } from 'react';
import { compose, pure, setPropTypes } from 'recompose';
import { default as styles } from './styles/main.css';
import { default as c } from 'classnames';

const keyValueElement = ({ className, name, active }) => (
  <div className={c(styles.base, className)}>
    <p className={styles.font}>{name}</p>
    <div className={active ? c(styles.circle, styles.active) : styles.circle}></div>
  </div>
);

export default compose(
  setPropTypes({
    name: PropTypes.string.isRequired,
    active: PropTypes.bool.isRequired,
    className: PropTypes.string,
  }),
  pure,
)(keyValueElement);
