import { default as React, PropTypes } from 'react';
import { compose, pure, setPropTypes, defaultProps } from 'recompose';
import { default as elementStyle } from '../common/card/styles/base.css';
import { default as styles } from './styles/main.css';
import { default as c } from 'classnames';

const display = ({ status, displayType }) => (
  <div className={c(elementStyle.base, elementStyle[displayType], styles.base )}>
    <ul className={styles.list}>
      <li>
        <div className={styles.title}>Value</div>
        <div className={styles.value}>{status.value}</div>
      </li>
      <li>
        <div className={styles.title}>Period</div>
        <div className={styles.value}>{status.period}</div>
      </li>
    </ul>
  </div>
);

export const propTypes = {
  displayType: PropTypes.oneOf(['none', 'preview', 'embeded']),
  status: PropTypes.object,
};

export default compose(
  defaultProps({
    status: { value: 0, period: 0 },
    displayType: 'none',
  }),
  setPropTypes(propTypes),
  pure,
)(display);
