import { default as React, PropTypes } from 'react';
import { compose, pure, renameProp, setPropTypes, defaultProps } from 'recompose';
import { default as Card } from '../common/card/';
import { default as DatachannelInfo } from '../common/datachannelInfo/index.js';
import { default as Display, propTypes as ControlPropTypes } from './Control';

const displayCard = ({
  numberOfCards,
  name,
  description,
  dataChnId,
  configs,
  detailButtonConfig,
  fullScreenConfig,
  recordedAt,
  ...props,
}) => (
  <Card
    detailButtonConfig={detailButtonConfig}
    fullScreenConfig={fullScreenConfig}
    configs={configs}
    numberOfCards={numberOfCards} >
    <div>
      <Display {...props} />
      <DatachannelInfo
        name={name}
        recordedAt={recordedAt}
        dataChnId={dataChnId}
        description={description}
      />
    </div>
  </Card>
);

export default compose(
  defaultProps({
    numberOfCards: 2,
    displayType: 'none',
  }),
  setPropTypes({
    configs: PropTypes.object.isRequired,
    numberOfCards: PropTypes.number,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    fullScreenConfig: PropTypes.object,
    recordedAt: PropTypes.number,
    detailButtonConfig: PropTypes.object,
    ...ControlPropTypes,
  }),
  pure,
  renameProp('value', 'status'),
)(displayCard);
