import React from 'react';
import Radium from 'radium';
import {PropTypes} from 'react';

class Nav extends React.Component {

  render() {
    let {
      style: listStyle,
      children,
      dropdownMenu,
      ...props
    } = this.props;

    return (
      <ul {...props} style={[
        dropdownMenu ? styles.dropdownMenu : styles.list,
        listStyle]}>
        {React.Children.map(children, this._renderChildren)}
      </ul>
    );
  }

  _renderChildren(child, index) {
    return React.cloneElement(child, {key: index});
  }
}

export default Radium(Nav);

Nav.propTypes = {
  style: PropTypes.object,
  dropdownMenu: PropTypes.bool
};
Nav.defaultProps = {};

const styles = {
  list: {
    display: 'inline-block',
    padding: 0,
    margin: 0
  },
  dropdownMenu: {
    display: 'none',
    minWidth: 100,
    padding: 0,
    zIndex: 1000,
    float: 'left',
    textAlign: 'center',
    listStyle: 'none',
    backgroundColor: '#1995cc',
    width: '100%'
  }
};
