import { default as React, PropTypes } from 'react';
import { compose, pure, setPropTypes, defaultProps, withAttachedProps } from 'recompose';
import { MiArrowExpand } from 'mtk-icon';
import { default as c } from 'classnames';
import { default as iconStyle } from '../icon/styles/main.css';

const detailButton = ({ config, ...props }) => {
  return (
    <a onClick={config.onFullScreen} {...props}>
      <MiArrowExpand className={iconStyle.icon} />
    </a>
  );
};

export default compose(
  defaultProps({
    active: false,
  }),
  setPropTypes({
    config: PropTypes.object.isRequired,
  }),
  pure,
  withAttachedProps((getProps) => ({
    config: getProps().infoConfig || [],
    className: c(
      iconStyle.base,
      getProps().className,
    ),
  })),
)(detailButton);
