import { default as React, PropTypes } from 'react';
import { compose, pure, setPropTypes, defaultProps } from 'recompose';
import { default as InputRange } from 'mtk-ui/lib/InputRange';
import { default as InputText } from 'mtk-ui/lib/InputText';
import { default as Button } from 'mtk-ui/lib/Button';
import { default as elementStyle } from '../common/card/styles/base.css';
import { default as styles } from './styles/main.css';
import { default as emptyFunction } from 'fbjs/lib/emptyFunction';
import { default as c } from 'classnames';

const control = ({ onClick, displayType }) => (
  <div className={c(elementStyle.base, elementStyle[displayType], styles.base, styles.controlBase )}>
    <ul className={styles.controlList} >
      <li>
        <div className={styles.title}>Period</div>
        <div className={styles.periodValue}><InputText placeholder="Interger only" style={{ width: 150 }} /><Button onClick={onClick}>OK</Button></div>
      </li>
      <li>
        <div className={styles.title}>Value</div>
        <InputRange style={{ width: '100%' }} />
      </li>
    </ul>
  </div>
);

export const propTypes = {
  status: PropTypes.object,
  displayType: PropTypes.oneOf(['none', 'preview', 'embeded']),
  onClick: PropTypes.func,
};

export default compose(
  defaultProps({
    status: { value: 0, period: 100 },
    onClick: emptyFunction,
    displayType: 'none',
  }),
  setPropTypes(propTypes),
  pure,
)(control);
