/**
 * hasScrollbar is a JavaScript function to determine if the current
 * window is scrollable or not, on a given axe or both.
 * reference: https://tylercipriani.com/2014/07/12/crossbrowser-javascript-scrollbar-detection.html
 *
 * @param {optional string} axe - The axe where the scroll is checked. Must be "x" or "y".
 * @return {boolean}
 */

// 把 footer 併上去的時候，有可能產生捲軸
let footerHeight = 160;

export const hasScrollbar = () => {
  let rootElem = document.documentElement || document.body;
  if (rootElem.scrollHeight + footerHeight > rootElem.clientHeight) {
    footerHeight = 0;
    return true;
  }
  else {
    footerHeight = 160;
    return false;
  }
};
