import { default as program } from 'commander';
import * as cli from '../scripts/cli.js';

program
  .command('generateSVGComponents <srcPatterns...>')
  .option('-o, --out-dir <path>', 'output directory for your generated React components')
  .option('-t, --template <path>', 'template that you want to generate [reactDefault, reactFunction]')
  .option('-b, --attrBlacklist <path>', 'blacklist attributes that you want to remove [Array]')
  .action(cli.generateSVGComponents);

program
  .command('generateComponentsEntry <srcPatterns...>')
  .option('--out-file [path]', 'Output dir')
  .action(cli.generateComponentsEntry);

program.parse(process.argv);
