export default function rccTemplate({ componentName, viewBox, jsx }) {
  return `
/**
 * Generator: mtk-icon generateSVGComponents cli
 * React class component template by default
 *
 * Feature: ES7, JSX
 *
 * @author Michael Hsu
 */

import { default as React, PropTypes } from 'react';
import pure from 'recompose/pure';

export const path = (
  <g>${jsx}</g>
);

@pure
export default class ${componentName} extends React.Component {
  static propTypes = {
    size: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.number,
    ]),
  }

  static defaultProps = {
    size: '1em',
  }

  render() {
    const {
      size,
      ...otherProps,
    } = this.props;

    return (
      <svg
        viewBox="${viewBox}"
        preserveAspectRatio="xMidYMid meet"
        fill="currentColor"
        width={size} height={size}
        {...otherProps}
      >
        {path}
      </svg>
    );
  }
}
`;
}
