export default function rccTemplate({ componentName, viewBox, jsx }) {
  return `
/**
 * Generator: mtk-icon generateSVGComponents cli
 * React functional component template by default
 *
 * ref: https://github.com/gorangajic/react-icon-base/blob/master/index.js
 */

import { default as React, PropTypes } from 'react';
import { default as compose } from 'recompose/compose';
import { default as pure } from 'recompose/pure';
import { default as defaultProps } from 'recompose/defaultProps';
import { default as setPropTypes } from 'recompose/setPropTypes';

const ${componentName} = ({ size, ...otherProps }) => (
  <svg
    viewBox="${viewBox}"
    preserveAspectRatio="xMidYMid meet"
    fit
    fill="currentColor"
    width={size} height={size}
    {...otherProps}
  >
    ${jsx}
  </svg>
);

export default compose(
  pure,
  defaultProps({
    size: '1em',
  }),
  setPropTypes({
    size: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.number,
    ]),
  }),
)(${componentName});
`;
}
